package maven1;

public class BreaknCountinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int i = 0; i<10; i++) {
			System.out.println(i);
			if(i==5){
			break;
			}
			
		    }
		for(int i = 0; i<10; i++) {
			if(i==5) {
				
			continue;
			}
		System.out.println(i);
				
			}
		
		for(int i = 1; i<10; i++) {
			for(int j = 1;j<10;j++) {
				System.out.println(i*j);
			}
			
		}
		}
		
		}




