public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=1;
		if(n>0) {
			System.out.println("number is +ve");
		}
		
		else if(n<0) {
			System.out.println("number is -ve");
		}
			else {
			System.out.println("number is zero");
			}
			
		}

	}


