package maven1;

public class loop123 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 0;i<=5;i++) {   
			System.out.println(i);
		}
		int j=0;
		while(j<=5) {
			System.out.println(j);
			j++;
		}
		
		for (int k=10;k>=1;k--) {
			System.out.println(k);
		}

	}

}
